<script>
    $('.delete').click(function () {
        var adminid = $(this).attr('data-id')
        var nama = $(this).attr('data-nama')

        swal({
                title: "Yakin ?",
                text: "Kamu Akan Menghapus Data Setoran Dengan Id Nama " + nama + "",
                icon: "warning",
                buttons: true,
                dangerMode: true,
            })
            .then((willDelete) => {
                if (willDelete) {
                    swal("Data Setoran " + nama + " Berhasil Di Hapus", {
                        icon: "success",
                    });
                    window.location = "/deletedata/" + adminid + ""
                } else {
                    swal("Data Setoran " + nama + " Tidak Jadi Di Hapus !", {
                        icon: "error",
                    });

                }
            });

    });

</script>

